(function(){
'use strict';
angular.module('keyManagement', ['ui.router'])

.config(["$stateProvider", "$urlRouterProvider", function($stateProvider, $urlRouterProvider){
  $urlRouterProvider.otherwise('/');
   $stateProvider
        .state('home', {
            url: '/',
            views : {
                'header': {
                  templateUrl: 'min_partials/header.html'
                },
                'content' : {
                    templateUrl: 'min_partials/content.html',
                    controller : 'mainController'
                },
                'footer' : {
                        templateUrl: 'min_partials/footer.html'
                }
          }
        })
}]);
})();

(function() {
    "use strict";

    angular.module('keyManagement').controller('mainController', ['$scope', function($scope) {
        console.log("main controller");
    }]);
})();
